package myJava;

public class IfElseProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int age=19;
	
	if(age<18) {
		System.out.println(age+""+"Voting is not allowed");
		
	}
	else {
		System.out.println(age+""+"Voting is allowed");
	}

	}

}
